    $rootScope.$on('menuSeclected', updateTableData);

    /**
     * 更新表格中的数据
     * @param {} e event事件
     * @param {*} menu 点选的菜单项
     * @param {*} currentMenu 已经选中的菜单项
     */
    function updateTableData(e, menu, currentMenu){
      
        if(!$state.is('app.package')) return;  
        // 解决切换Url时，菜单和数据不匹配的问题
        initUrl = getCurrentPkgUrl(currentMenu);

        var level = menu.level;
        var tmpUrl = '';
        // 接收选中菜单项

        switch(level){
          case 1:
            if(menu.name === '老云'){
              $scope.isOldCloud = true;
            }else{
              $scope.isOldCloud = false;
            }
            if(menu.child.length){
              tmpUrl = menu.child[0] && menu.child[0].url;
            }else{
              tmpUrl =  menu.url;
            }
            break;
          default:
           tmpUrl = menu.url;
        }

        var url = initUrl?initUrl:tmpUrl;
        // 获取表格数据
        $scope.getContentDetail(url);
    }

    // 递归找到切换 0 级目录前的菜单url
    function getCurrentPkgUrl(originMenu, loopMenu, level){
      var url = '';
      var loop = ['firstMenu','secMenu','thirdMenu'];
      level = level || 0;
      loopMenu = loopMenu || originMenu.child;

      if(loopMenu.length){
        for(var i=0;i<loopMenu.length;i++){
          if(originMenu[loop[level]] == loopMenu[i].id){
            if(!loopMenu[i].child.length){
              url = loopMenu[i].url;
            }else{
              url = getCurrentPkgUrl(originMenu,loopMenu[i].child, ++level)
            }
            break;
          }
        }
      }
      return url;
    }

